sqlMap.insert:
/* adm8000DAO.insertAdm8000MasterInfo - 보고서 마스터 등록 */
SELECT LPAD( TO_NUMBER( COALESCE( MAX( REPORT_CD),0) )+1 ,2,'0') AS NEW_REPORT_CD FROM RPT1000 WHERE 1=1 AND LIC_GRP_ID = #licGrpId# AND PRJ_ID = #prjId# AND REPORT_YEAR = #reportYear#
/* adm8000DAO.insertAdm8000MasterInfo - 보고서 마스터 등록 */ INSERT INTO RPT1000 ( LIC_GRP_ID , /* 라이선스그룹 ID */ PRJ_ID , /* 프로젝트 ID */ REPORT_YEAR , /* 기준연도 */ REPORT_CD , /* 보고서 구분 코드 */ REPORT_NM , /* 보고서 구분명 */ CONF_YN , /* 확장 여부 */ REG_DTM , /* 최초등록 일시 */ REG_USR_ID , /* 최초등록자 ID */ REG_USR_IP , /* 최초등록자 IP */ MODIFY_DTM , /* 최종수정일시 */ MODIFY_USR_ID , /* 최종수정자 ID */ MODIFY_USR_IP /* 최종수정자 IP */ ) VALUES ( #licGrpId#, #prjId#, #reportYear#, #newReportCd#, #reportNm#, #confYn# , CURRENT_TIMESTAMP, #regUsrId#, #regUsrIp#, CURRENT_TIMESTAMP, #modifyUsrId#, #modifyUsrIp# )

/* adm8000DAO.insertAdm8000MasterInfo - 보고서 마스터 등록 */
SELECT LPAD( TO_NUMBER( COALESCE( MAX( ITEM_CD),0) )+1 ,2,'0') AS NEW_ITEM_CD FROM RPT1100 WHERE 1=1 AND LIC_GRP_ID = #licGrpId# AND PRJ_ID = #prjId# AND REPORT_YEAR = #reportYear# AND REPORT_CD = #reportCd#
/* adm8000DAO.insertAdm8000DetailInfo - 보고서 디테일 등록 */ INSERT INTO RPT1100 ( LIC_GRP_ID , /* 라이선스그룹 ID */ PRJ_ID , /* 프로젝트 ID */ REPORT_YEAR , /* 기준연도 */ REPORT_CD , /* 보고서 구분 코드 */ ITEM_CD , /* 측정항목 코드 */ INDEX_CD , /* 지표구분 코드 */ ITEM_NM , /* 측정항목 명 */ WEIGHT_VAL , /* 가중치 */ PERIOD_CD , /* 보고./평가주기 코드 */ PERIOD_ORD , /* 순번 */ PROCESS_ID , /* 프로세스 ID */ ITEM_TYPE_CD , REG_DTM , /* 최초등록 일시 */ REG_USR_ID , /* 최초등록자 ID */ REG_USR_IP , /* 최초등록자 IP */ MODIFY_DTM , /* 최종수정일시 */ MODIFY_USR_ID , /* 최종수정자 ID */ MODIFY_USR_IP /* 최종수정자 IP */ ) VALUES ( #licGrpId#, #prjId#, #reportYear#, #reportCd#, #newItemCd#, #indexCd#, #itemNm#, #weightVal#, #periodCd#, #periodOrd#, #processId#, #itemTypeCd#, CURRENT_TIMESTAMP, #regUsrId#, #regUsrIp#, CURRENT_TIMESTAMP, #modifyUsrId#, #modifyUsrIp# )
