sqlMap.insert:
/* dpl2100DAO.insertDpl2100DplSignInfo - 배포 계획 결재 ID 발급 */ SELECT COALESCE( SUBSTR(NEW_SIGN_ID, 1, 11) || LPAD( (TO_NUMBER(SUBSTR(NEW_SIGN_ID, 12, 5)) + 1) , 5, '0') , 'SIG' || TO_CHAR(CURRENT_TIMESTAMP, 'YYYYMMDD') || '00001' ) AS NEW_DPL_ID FROM ( SELECT MAX(SIGN_ID) AS NEW_SIGN_ID FROM DPL1200 A WHERE 1=1 AND A.PRJ_ID = #prjId# AND A.DPL_ID = #dplId# AND A.SIGN_ID LIKE 'SIG' || TO_CHAR(CURRENT_TIMESTAMP, 'YYYYMMDD') || '%' )
/* dpl2100DAO.insertDpl2100DplSignInfo - 배포계획 결재 등록 */ INSERT INTO DPL1200 ( PRJ_ID ,DPL_ID ,SIGN_ID ,SIGN_USR_ID ,SIGN_TXT ,SIGN_STS_CD ,SIGN_REJECT_TXT ,SIGN_DTM ,SIGN_REG_USR_ID ,REG_DTM ,REG_USR_ID ,REG_USR_IP
) VALUES ( #prjId# ,#dplId# ,#newSignId# ,#signUsrId# ,#signTxt# ,#signStsCd# ,#signRejectTxt# ,CURRENT_TIMESTAMP ,#signRegUsrId# ,CURRENT_TIMESTAMP ,#regUsrId# ,#regUsrIp# )
