/* adm2000DAO.prs4000DAO.insertPrs4000SchdInfo 일정 등록 */
INSERT INTO PRS4000( USR_ID , SCHD_SEQ , SCHD_YMD_ST, SCHD_YMD_ED, SCHD_CONTENTS, REG_DTM, REG_USR_ID, REG_USR_IP, MODIFY_DTM, MODIFY_USR_ID, MODIFY_USR_IP )VALUES( #usrId#, ( SELECT COALESCE(MAX(schd_seq)+1,1) FROM PRS4000 WHERE USR_ID = #usrId# ), #startDt#, #endDt#, #schdContents#, CURRENT_TIMESTAMP, #usrId#, #usrIp#, CURRENT_TIMESTAMP, #usrId#, #usrIp# )
SELECT COALESCE(MAX(schd_seq),1) as schdSeq FROM PRS4000 WHERE USR_ID = #usrId#
