sqlMap.select:
/* req4500DAO.selectReq4500ReqHistoryList - 요구사항 변경 히스토리 정보 가져오기 */ SELECT SUBT2.* FROM ( SELECT SUBT1.TYPE ,SUBT1.PRJ_ID ,SUBT1.REQ_ID ,SUBT1.REQ_NM ,SUBT1.REQ_CHG_ID ,SUBT1.REQ_CHG_TYPE ,SUBT1.CHG_USR_ID ,COALESCE( (SELECT USR_NM FROM ADM2000 WHERE USR_ID = SUBT1.CHG_USR_ID) , ' ') AS CHG_USR_NM ,COALESCE( (SELECT EMAIL FROM ADM2000 WHERE USR_ID = SUBT1.CHG_USR_ID) , ' ') AS CHG_USR_EMAIL ,SUBT1.CHG_DTM ,SUBT1.PROCESS_ID ,SUBT1.PROCESS_NM ,SUBT1.PRE_FLOW_ID ,SUBT1.PRE_FLOW_NM ,SUBT1.PRE_FLOW_ESSENTIAL_CD ,SUBT1.PRE_FLOW_SIGN_CD ,SUBT1.PRE_FLOW_SIGN_STOP_CD ,SUBT1.PRE_FLOW_END_CD ,SUBT1.PRE_FLOW_WORK_CD ,SUBT1.PRE_FLOW_NEXT_ID ,SUBT1.PRE_FLOW_TITLE_BG_COLOR ,SUBT1.PRE_FLOW_TITLE_COLOR ,SUBT1.PRE_FLOW_CONTENT_BG_COLOR ,SUBT1.PRE_FLOW_CONTENT_COLOR ,SUBT1.CHG_FLOW_ID ,SUBT1.CHG_FLOW_NM ,SUBT1.CHG_FLOW_ESSENTIAL_CD ,SUBT1.CHG_FLOW_SIGN_CD ,SUBT1.CHG_FLOW_SIGN_STOP_CD ,SUBT1.CHG_FLOW_END_CD ,SUBT1.CHG_FLOW_WORK_CD ,SUBT1.CHG_FLOW_NEXT_ID ,SUBT1.CHG_FLOW_TITLE_BG_COLOR ,SUBT1.CHG_FLOW_TITLE_COLOR ,SUBT1.CHG_FLOW_CONTENT_BG_COLOR ,SUBT1.CHG_FLOW_CONTENT_COLOR ,SUBT1.PRE_CHARGER_ID ,COALESCE ( (SELECT USR_NM FROM ADM2000 WHERE USR_ID = SUBT1.PRE_CHARGER_ID) , ' ') AS PRE_CHARGER_NM ,SUBT1.CHG_CHARGER_ID ,COALESCE ( (SELECT USR_NM FROM ADM2000 WHERE USR_ID = SUBT1.CHG_CHARGER_ID) , ' ') AS CHG_CHARGER_NM ,SUBT1.REG_DTM FROM ( SELECT 'CHG' AS TYPE ,A.PRJ_ID ,A.REQ_ID ,D.REQ_NM ,A.REQ_CHG_ID ,A.REQ_CHG_TYPE ,A.CHG_USR_ID ,A.CHG_DTM ,A.PROCESS_ID ,E.PROCESS_NM ,A.PRE_FLOW_ID ,B.FLOW_NM AS PRE_FLOW_NM ,B.FLOW_ESSENTIAL_CD AS PRE_FLOW_ESSENTIAL_CD ,B.FLOW_SIGN_CD AS PRE_FLOW_SIGN_CD ,B.FLOW_SIGN_STOP_CD AS PRE_FLOW_SIGN_STOP_CD ,B.FLOW_END_CD AS PRE_FLOW_END_CD ,B.FLOW_WORK_CD AS PRE_FLOW_WORK_CD ,B.FLOW_NEXT_ID AS PRE_FLOW_NEXT_ID ,B.FLOW_TITLE_BG_COLOR AS PRE_FLOW_TITLE_BG_COLOR ,B.FLOW_TITLE_COLOR AS PRE_FLOW_TITLE_COLOR ,B.FLOW_CONTENT_BG_COLOR AS PRE_FLOW_CONTENT_BG_COLOR ,B.FLOW_CONTENT_COLOR AS PRE_FLOW_CONTENT_COLOR ,A.CHG_FLOW_ID ,C.FLOW_NM AS CHG_FLOW_NM ,C.FLOW_ESSENTIAL_CD AS CHG_FLOW_ESSENTIAL_CD ,C.FLOW_SIGN_CD AS CHG_FLOW_SIGN_CD ,C.FLOW_SIGN_STOP_CD AS CHG_FLOW_SIGN_STOP_CD ,C.FLOW_END_CD AS CHG_FLOW_END_CD ,C.FLOW_WORK_CD AS CHG_FLOW_WORK_CD ,C.FLOW_NEXT_ID AS CHG_FLOW_NEXT_ID ,C.FLOW_TITLE_BG_COLOR AS CHG_FLOW_TITLE_BG_COLOR ,C.FLOW_TITLE_COLOR AS CHG_FLOW_TITLE_COLOR ,C.FLOW_CONTENT_BG_COLOR AS CHG_FLOW_CONTENT_BG_COLOR ,C.FLOW_CONTENT_COLOR AS CHG_FLOW_CONTENT_COLOR ,A.PRE_CHARGER_ID ,A.CHG_CHARGER_ID ,A.REG_DTM FROM REQ4700 A LEFT JOIN FLW1100 B ON ( A.PRJ_ID = B.PRJ_ID AND A.PROCESS_ID = B.PROCESS_ID AND A.PRE_FLOW_ID = B.FLOW_ID) LEFT JOIN FLW1100 C ON ( A.PRJ_ID = C.PRJ_ID AND A.PROCESS_ID = C.PROCESS_ID AND A.CHG_FLOW_ID = C.FLOW_ID ) LEFT JOIN REQ4100 D ON ( A.PRJ_ID = D.PRJ_ID AND A.REQ_ID = D.REQ_ID ) LEFT JOIN FLW1000 E ON ( A.PRJ_ID = E.PRJ_ID AND A.PROCESS_ID = E.PROCESS_ID ) WHERE 1=1 AND A.PRJ_ID = #prjId# AND A.REG_DTM BETWEEN TO_DATE( #startDt# ,'YYYY-MM-DD' ) AND TO_DATE( #endDt# ,'YYYY-MM-DD' ) +0.99999
AND A.REQ_ID = #reqId#
) SUBT1 ) SUBT2 WHERE 1 = 1
AND SUBT2.CHG_USR_NM LIKE '%'|| #searchTxt# ||'%'

/* req4500DAO.selectReq4500ChgDetailList - 요구사항 수정이력 목록 가져오기 */ SELECT SUBT1.* FROM ( SELECT 'MOD' AS TYPE , A.PRJ_ID, A.REQ_ID, A.CHG_DETAIL_ID, A.CHG_DETAIL_NUM ,A.CHG_DETAIL_TYPE, A.CHG_DETAIL_NM ,CASE A.CHG_DETAIL_OPT_TYPE WHEN '02' THEN ( SELECT SUB_CD_NM FROM ADM4100 WHERE LIC_GRP_ID = #licGrpId# AND MST_CD = A.CHG_DETAIL_COMMON_CD AND SUB_CD = A.PRE_DETAIL_VAL ) WHEN '03' THEN ( SELECT USR_NM FROM ADM2000 WHERE USR_ID = A.PRE_DETAIL_VAL) ELSE A.PRE_DETAIL_VAL END AS PRE_DETAIL_VAL ,CASE A.CHG_DETAIL_OPT_TYPE WHEN '02' THEN ( SELECT SUB_CD_NM FROM ADM4100 WHERE LIC_GRP_ID = #licGrpId# AND MST_CD = A.CHG_DETAIL_COMMON_CD AND SUB_CD = A.CHG_DETAIL_VAL ) WHEN '03' THEN ( SELECT USR_NM FROM ADM2000 WHERE USR_ID = A.CHG_DETAIL_VAL) ELSE A.CHG_DETAIL_VAL END AS CHG_DETAIL_VAL ,( SELECT USR_NM FROM ADM2000 WHERE USR_ID = A.REG_USR_ID) AS REG_USR_NM ,( SELECT EMAIL FROM ADM2000 WHERE USR_ID = A.REG_USR_ID) AS REG_USR_EMAIL ,( SELECT USR_IMG_ID FROM ADM2000 WHERE USR_ID = A.REG_USR_ID) AS REG_USR_IMG ,A.REG_DTM, A.REG_USR_ID, A.REG_USR_IP , ( SELECT REQ_NM FROM REQ4100 WHERE PRJ_ID = A.PRJ_ID AND REQ_ID = A.REQ_ID ) AS REQ_NM FROM REQ4800 A WHERE 1=1 AND A.PRJ_ID = #prjId# AND A.REG_DTM BETWEEN TO_DATE( #startDt# ,'YYYY-MM-DD' ) AND TO_DATE( #endDt# ,'YYYY-MM-DD' ) +0.99999
AND A.REQ_ID = #reqId#
) SUBT1 WHERE 1 = 1
AND SUBT1.REG_USR_NM LIKE '%'|| #searchTxt# ||'%'
